import React, {useState, useEffect, useContext} from 'react'
import {Redirect} from 'react-router-dom'
import ArticleForm from 'components/articleForm'
import useFetch from 'hooks/useFetch'
import {CurrentUserContext} from 'contexts/currentUser'

const CreateArticle = () => {

    const apiUrl = '/articles'
    const [isSuccessfulSubmit, setIsSuccessfulSubmit] = useState(false)
    const [{response, error}, doFetch] = useFetch(apiUrl)
    const [currentUserState] = useContext(CurrentUserContext)

    console.log('current user state', currentUserState)

    const onSubmit = article => {
        
        doFetch({
            method: 'post',
            data: {
                article
            }
        })
    }

    const initialValues = {
        title: '',
        description: '',
        body: '',
        tagList: ''
    }

    useEffect(() => {
        if(!response){
            return
        }
        setIsSuccessfulSubmit(true)
    }, [response])

    if(currentUserState.isLoggedIn === null) {
        return null
    } 

    // if(!currentUserState.isLoggedIn === false){
    //     return <Redirect to="/" />
    // }

    if(isSuccessfulSubmit || currentUserState.isLoggedIn === false) {//submitできた場合、もしくはログインしていない場合にはホームに戻る
        return <Redirect to="/" />
    }

    return (
        <div>
            <ArticleForm 
            onSubmit={onSubmit}
            initialValues={initialValues}
            errors={(error && error.errors) || []}//error.eroors = backendでerrorを起こすerror
            />
        </div>
    )
    
}

export default CreateArticle